mapper.select:
SELECT c.*, u.username, u.nickname, u.avatar
FROM t_comment c
         LEFT JOIN t_user u ON c.user_id = u.id
WHERE c.comic_id = #{comicId}
ORDER BY c.create_time DESC
    LIMIT #{offset}, #{size}

SELECT c.*, u.username, u.nickname, u.avatar
FROM t_comment c
         LEFT JOIN t_user u ON c.user_id = u.id
WHERE c.chapter_id = #{chapterId}
ORDER BY c.create_time DESC
    LIMIT #{offset}, #{size}
